method.code:
	public function DisplayBareRelations(WebPage $oP, $bEditMode = false)
	{
        parent::DisplayBareRelations($oP, $bEditMode);

        if (! $bEditMode)
        {
            $iDBServerId = $this->GetKey();
            $sType = $this->Get('type');
            switch ($sType) {
                case 'mariadb':
                    $sOQL = "SELECT AzureMariaDB WHERE azuredbserver_id = :id";
                    break;

                case 'postgre':
                    $sOQL = "SELECT AzurePostgreSQL WHERE azuredbserver_id = :id";
                    break;

                case 'mysql':
                    $sOQL = "SELECT AzureMySQL WHERE azuredbserver_id = :id";
                    break;

                case 'mssql':
                    $sOQL = "SELECT AzureMSSQL WHERE azuredbserver_id = :id";
                    break;

                default:
                    $sOQL = '';
                    break;
            }
            if ($sOQL != '') {
                $oLnkSet =  new CMDBObjectSet(DBObjectSearch::FromOQL($sOQL), array(), array('id' => $iDBServerId));
                $iDBs = $oLnkSet->Count();
                if ($iDBs > 0)
                {
                    $oP->SetCurrentTab(Dict::S('Class:AzureDBServer/Tab:azuredatabases_list').' ('.$iDBs.')');
                    $oP->p(MetaModel::GetClassIcon('AzureDB').'&nbsp;'.Dict::S('Class:AzureDBServer/Tab:azuredatabases_list+'));
                    $oBlock = new DisplayBlock($oLnkSet->GetFilter(), 'list', false);
                    $oBlock->Display($oP, 'dbserver_db', array('menu' => false, 'view_link' => false));
                }
            }
            if (($sOQL == '') || ($iDBs == 0)) {
                $oP->SetCurrentTab(Dict::S('Class:AzureDBServer/Tab:azuredatabases_list'));
                $oP->p(MetaModel::GetClassIcon('AzureDB').'&nbsp;'.Dict::S('UI:NoObjectToDisplay'));
            }
	    }
	}

