class AzureExtraMenus implements iPopupMenuExtension
{
	const MODULE_NAME = 'combodo-azure-datamodel';
	const AZURE_DEFAULT_MENU_ICON = 'fas fa-cloud-upload-alt';

	/**
	 * @inheritdoc
	 */
	public static function EnumItems($iMenuId, $param) {
		$aResult = array();
		switch ($iMenuId) {
			case iPopupMenuExtension::MENU_OBJLIST_ACTIONS: // $param is a DBObjectSet
			case iPopupMenuExtension::MENU_OBJLIST_TOOLKIT: // $param is a DBObjectSet
				break;

			case iPopupMenuExtension::MENU_OBJDETAILS_ACTIONS: // $param is a DBObject
				$oObj = $param;

				if (($oObj instanceof AzureCI) || ($oObj instanceof AzureSubscription) || ($oObj instanceof AzureResourceGroup)){
					// Add URL toward Azure objects using Tenant's name
					$sTenantName = $oObj->Get('azuretenant_name');
					if ($sTenantName != '') {
        				$aDirectAccessParams = MetaModel::GetModuleSetting(static::MODULE_NAME, 'direct_access', array());
		        		if (!empty($aDirectAccessParams)) {
				        	if (array_key_exists('label', $aDirectAccessParams) && array_key_exists('url', $aDirectAccessParams)) {
				                $sLabel = $aDirectAccessParams['label'];
				                $sUrl = $aDirectAccessParams['url'];
				                if (($sUrl != '') && ($sLabel != '')) {
				                    // Build URL
				                    $sUrl = str_replace('$azureid$', $oObj->Get('azureid'), $sUrl);
				                    $sUrl = str_replace('$azuretenant$', $sTenantName, $sUrl);

				                    // Build tooltip
				                    $sTooltip = array_key_exists('tooltip', $aDirectAccessParams) ? $aDirectAccessParams['tooltip'] : '';

				                    // Build icon
				                    $sIcon = array_key_exists('icon', $aDirectAccessParams) ? $aDirectAccessParams['icon'] : static::AZURE_DEFAULT_MENU_ICON;

				                    // Build target page
				                    $sTarget = array_key_exists('target', $aDirectAccessParams) ? $aDirectAccessParams['target'] : '_blank';

				                    $oButton = new URLButtonItem('azure_integration', $sLabel, $sUrl, $sTarget);
				                    if (version_compare(ITOP_DESIGN_LATEST_VERSION, '3.0', '>=')) {
				                        $oButton->SetIconClass($sIcon);
				                        $oButton->SetTooltip($sTooltip);
				                    }
				                    $aResult[] = $oButton;
				                }
			        		}
		        		}
					}
				}
				break;

			case iPopupMenuExtension::MENU_DASHBOARD_ACTIONS: // $param is a Dashboard
			default:
				break;
		}

		return $aResult;
	}
}
